(function($){
    $.fn.content_slider = function(options){
        var settings = $.extend({
            map: [],
            max_shown_items: 7,
            hv_switch: 0,
            active_item: 0,
            wrapper_text_max_height: 450,
            middle_click: 1,
            border_radius: -1,
            automatic_height_resize: 1,
            border_on_off: 1,
            border_color: '#DDDDDD',
            use_thin_arrows: 1,
            small_border: 2,
            big_border: 5,
            big_pic_width: 200,
            big_pic_height: 200,
            plugin_url: ''
        }, options );

        var container = this;
        var currentIndex = settings.active_item;

        function showSlide(index){
            container.find('.circle_slider_text_wrapper').hide();
            container.find('.circle_slider_text_wrapper').eq(index).show();
        }

        showSlide(currentIndex);

        return this;
    };
})(jQuery);